export const getRandomElement = (list: unknown[]) => {
  if (list.length === 0) {
    return undefined; // Return undefined for an empty list
  }
  const randomIndex = Math.floor(Math.random() * list.length);
  const res = list[randomIndex];
  return res;
};
export const getRandomSpeedyCard = () => getRandomElement(samples) + footer;
